package main

import (
	"fmt"
	"math"
)

func longestPalindrome(s string) string {
	if len(s) < 1 {
		return ""
	}
	start := 0
	end := 0

	for i := 0; i < len(s); i++ {
		max := int(math.Max(float64(expandPalindrome(s, i, i)), float64(expandPalindrome(s, i, i+1))))

		if max > end-start {
			start = i - (max-1)/2
			end = i + (max / 2)
		}
	}

	return s[start : end+1]
}

func expandPalindrome(s string, left int, right int) int {
	for left > -1 && right < len(s) && s[left] == s[right] {
		left--
		right++
	}
	return right - left - 1
}

func main() {
	fmt.Println(longestPalindrome("babad"))
	fmt.Println(longestPalindrome("abb"))
	fmt.Println(longestPalindrome(""))
	fmt.Println(longestPalindrome(""))
}
